import React from 'react'
import Tasks from './Tasks.js'

class CategoryFilters extends React.Component{


    render(){
        let taskItem = this.props.tasks.map(task => (
            <Task key={task.text} task={task} />
        ))
        return(
            <div>
                <h5>Tasks</h5>
                <ul>
                    <li>{taskItem}</li>
                </ul>
            </div>
        )
    }

}

export default CategoryFilters
