import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import CategoryFilters from './CategoryFilters.js'
import TaskList from './TaskList.js'

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ], 
    selectedCat: 'All'
  }

  // filterList = (e) => {
  //   console.log(e.target)

  // }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <div>
          <CategoryFilters filter={this.filterList} categories={CATEGORIES} /> 
          <TaskList filter={this.filterList} categories={CATEGORIES} tasks={this.state.selectedCat}/> 
        </div>
      </div>
    );
  }
}

export default App;
