import React from 'react'
import TaskList from './TaskList.js'

class CategoryFilters extends React.Component{


    render(){
        let taskItem = this.props.tasks.map(task => (
            <Task className="task" key={task.text} task={task} />
        ))
        let buttonCat = this.props.tasks.map(task => (
            <button>{task.category}</button>
        ))
        return(
            <div>
                <div className="categories">
                    <h5>Categories</h5>
                    <button>{buttonCat}</button>
                </div>
                <div className="tasks">
                    <TaskList />
                </div>
            </div>
        )
    }

}

export default CategoryFilters
